from __future__ import (absolute_import, division, print_function,unicode_literals)

from .get import Bar

def get_bar(symbol, ktype,freq, cachetime,renew,cache=True,source='',token=''):
    result = Bar(symbol=symbol, ktype=ktype,freq=freq, cachetime=cachetime,renew=renew,source=source,token=token)
    return result

def quote(symbol, ktype='quote', freq='1d', start='', end='', limit=1000, renew=True, cachetime=86400,source='',token='', fields=[]):
    result = Bar(symbol=symbol, ktype=ktype, freq=freq, start=start, end=end, limit=limit, cachetime=cachetime, renew=renew,source=source,token=token, fields=fields).quote()
    return result

